string longestCommonSubstring(const string& s1, const string& s2) 
{ 
    int n1 = s1.length(); 
    int n2 = s2.length(); 

    int maxlen = 0;
    int endingIndex = n1;

    int dp[n1 + 1][n2 + 1]; 

    for (int i = 0; i <= n1; i++) { 
        for (int j = 0; j <= n2; j++) { 
            if (i == 0 || j == 0) { 
                dp[i][j] = 0; 
            } else if (s1[i - 1] == s2[j - 1]) { 
                dp[i][j] = dp[i - 1][j - 1] + 1; 

                if (dp[i][j] > maxlen) { 
                    maxlen = dp[i][j]; 
                    endingIndex = i; 
                } 
            } else { 
                dp[i][j] = 0; 
            } 
        } 
    } 

    return s1.substr(endingIndex - maxlen, maxlen); 
}

